sp_widget.client_script:
function($scope, $rootScope) {
	var c = this;
	$rootScope.$on('user', function(event, args) {
		$scope.data.id = args.id;
		c.server.update();
	});
}

sp_widget.script:
(function() {
	var information = {};
	var user = gs.getUser();

	if(input.id) {
	user = user.getUserByID(input.id);
}

	information.name = user.getName();
	//information.full = user.getFullName();
	information.email = user.getEmail();
	data.information = information;

	var roles = [];
	roles = user.getUserRoles().toString().split(',');
	data.roles = roles;

	var groups = [];
	var groupGlide = new GlideRecord('sys_user_grmember');
	groupGlide.addQuery('user', user.getID());
	groupGlide.query();

	while(groupGlide.next()){
		group = {};
		group.name = groupGlide.getDisplayValue('group');
		groups.push(group);
	}
	data.groups = groups;
})();
