sys_script_include.script:
var SingletonScriptInclude = (function () {

	/*
    ** Instance Variables
    */

	/*	
		gs.addInfoMessage(SingletonScriptInclude.getValue('number'));
		=> Security restricted: Read operation on table 'task' from scope 'My Hotel' was granted and added to 'My Hotel' cross scope privileges
Background message, type:info, message: TOLTASK0001001

		gs.addInfoMessage(SingletonScriptInclude.getValue('short_description'));

		=> Background message, type:info, message: Ready for fulfillment

		gs.addInfoMessage(SingletonScriptInclude.getValue('number'));

		=> Background message, type:info, message: TOLTASK0001001		
    */	

    var grSingleInstance;

    return {

        /*
        ** Public functions
        */

        getValue: function (label) {
            var fieldValue = '';

            if (gs.nil(grSingleInstance)) {
                grSingleInstance = getGR();
            }

            if (!gs.nil(grSingleInstance) && grSingleInstance.isValid() ) {
                fieldValue = grSingleInstance.getValue(label);
                if (gs.nil(fieldValue)) {
                    fieldValue = '';
                } 
            }

            return fieldValue;
        }
    };

    /*
    ** Private functions
    */

    function getGR() {
        var gr = new GlideRecord('task');  
        gr.addQuery('active',true);
        gr.setLimit(1);
        gr.query(); 
		if (!gr.next()) {
            throw("Could not find GR");
        }
        return gr;
    }

})();
